/**
 * Play the track.
 * @method KEN.PlaylistTrack#play
 */
KEN.PlaylistTrack.prototype.play = function()
{
    if(this._sound === null)
    {
        this._sound = new KEN.Sound(this._viewer, this._uid + "-sound", this._url);
        this._sound["volume"] = this._viewer["playlists"]["volume"];

        this._bindEvents();
    }    

    this._sound.play();
};

